import React, { Component } from 'react';

import List from './List';

export default class KanbanBoard extends Component {
    constructor() {
        super(...arguments);

        this.state = {
            cards: [],
        }
    }

    addNewCardHandler = (cards) => {
        let cardIndex = cards.findIndex((card)=>card.id === cards.id);
        let card = cardIndex[cards];
        cards.splice(cardIndex, 0, card);
        this.setState({cards: cards});
    }

    render() {
        return(
            <div className="KanbanBoard">
                <List 
                    title="To Do:"
                    cards={this.props.cards.filter(card => card.status === "to-do")}
                    addNewCard={this.addNewCardHandler(this.props.cads).bind(this)} />
                <List 
                    title="In Progress:"
                    cards={this.props.cards.filter(card => card.status === "in-progress")} />
                <List 
                    title="Completed:"
                    cards={this.props.cards.filter(card => card.status === "completed")} />
            </div>
        )
    }
}
