import React, { Component } from 'react';

import NewTaskList from './NewTaskList';

export default class NewCardForm extends Component {
    constructor() {
        super();

        this.state = {
            inputValue: "",
        }

        this.updateInputValue = this.updateInputValue.bind(this);
    }

    updateInputValue = (e) => {
        let updatedInput = e.target.value;
        this.setState({inputValue: updatedInput});
    }

    cancelAdding = (e) => {
        return false;
    }

    handleSubmit = (e) => { e.preventDefault() }

    render() {
        return (
            <div className="NewCard" onSubmit={this.handleSubmit}>
                <label htmlFor="card-title">Card Title</label>
                <input 
                    type="text" 
                    value={this.state.inputValue}
                    placeholder="..." 
                    className="NewCard__title"
                    onChange={(e) => this.updateInputValue(e)} />
                <label htmlFor="card-description">Card Description</label>
                <textarea 
                    placeholder="..." 
                    className="NewCard__description" />
                <NewTaskList />
                <div>
                    <button 
                        type="submit"
                        className="NewCard__btn add btn"
                        onClick={this.props.newCard}>+</button>
                    <a 
                        href="/#"
                        className="NewCard__btn cancel"
                        onClick={this.props.addNewClicked}>cancel</a>
                </div>
            </div>
        )
    }
}
