import React, { Component } from 'react';

import TaskList from './TaskList';

export default class Card extends Component {
    render() {
        const getLevelClass = (level) => {
            if(level === 'low') {
                return 'level--low'
            } else if(level === 'medium') {
                return 'level--medium'
            } else return 'level--high'
        }
        const levelClass = getLevelClass(this.props.level);

        const checkTask = (task) => {
            if(task === null) {
                return null;
            } else return <TaskList tasks={task}/>
        }

        const checkbox = checkTask(this.props.tasks);
        return(
            <div className={`Card ${levelClass}`} >
                <div>
                    <h3 className={"Card__title"}>
                        {this.props.cardTitle}
                        <button 
                            className="Card__title--delete btn"
                            onClick={this.props.deleteCard}>delete</button>
                    </h3>
                </div>
                <span className="Card__description">{this.props.description}</span>
                {/* <TaskList tasks={this.props.tasks}/>  */}
                {checkbox}
                <span className="level">{this.props.level}</span>
            </div>
        )
    }
}
