import React, { Component } from 'react';

import Card from './Card';

export default class List extends Component {
    constructor() {
        super();

        this.state = {
            inputValue: "",
            submitted: false,
            cards: [],
        };

        this.updateInputValue = this.updateInputValue.bind(this);
        this.deleteCardHandler = this.deleteCardHandler.bind(this);
        this.addNewCardHandler = this.addNewCardHandler.bind(this);
    }

    updateInputValue = (e) => {
            let updatedValue = e.target.value;
            this.setState({inputValue: updatedValue, submitted: true});
            console.log(this.state.inputValue);
    }

    // addNewCardHandler = (e, cardID, cardTitle) => {
    //     let cards = this.props.cards;
    //     cards.concat(cardTitle)

    //     this.setState
    // }

    addNewCardHandler = (e, newCardTitle, cardID) => {

    }

    // addNewCardHandler(e, this.state.inputValue)

    deleteCardHandler = (cardID) => {
        let cards = this.props.cards;
        let cardToDelete = cards.indexOf(cardID);
        cards.splice(cardToDelete, 1);
        this.setState({cards: cards});
    }

    newCardForm = (e) => {
        return (
            <form>
                <input type="text" placeholder="Enter card title..." />
                <textarea placeholder="Enter card description..." />
                {/* tasks */}
            </form>
        )
    }

    render() {
        let cards = this.props.cards.map((card) => {
            return ( 
                <Card  
                    {...card} 
                    key={card.id} 
                    deleteCard={() => this.deleteCardHandler(card.id)} />
            )
        });

        return(
            <div className="List">
                <h1 className="List__title">{this.props.title}</h1>
                {cards}
                {/* <h1>{newCardTitle}</h1> */}
                    {/* <input 
                        type="text" 
                        value={this.state.inputValue}
                        placeholder="Add new task"
                        onChange={(e) => this.updateInputValue(e)} /> */}
                <button 
                    type="button" 
                    className="List__newCard--btn"
                    onClick={(e) => this.newCardForm(e).bind(this)} >Add new card</button>
            </div>
        )
    }
}
