import React, { Component } from 'react';

import Card from './Card';

export default class List extends Component {
    constructor() {
        super();

        this.state = {
            inputValue: '',
        };

        this.updateInputValue = this.updateInputValue.bind(this);
        this.addNewTaskHandler = this.addNewTaskHandler.bind(this);
    }

    updateInputValue = (e) => {
        this.setState({inputValue: e.event.value});
    }

    addNewTaskHandler = (e, taskTitle) => {
        return console.log(taskTitle);
    }

    render() {
        let cards = this.props.cards.map((card) => {
            return ( <Card  {...card} key={card.id}/>
                // <Card id={card.id} 
                //     key={card.id}
                //     cardTitle={card.cardTitle}
                //     description={card.description}
                //     status={card.status}
                //     level={card.level}
                //     tasks={card.taskTitle} /> 
            )
        });

        const newTaskTitle = this.state.inputValue;

        return(
            <div className="List">
                <h1 className="List__title">{this.props.title}</h1>
                {cards}
                <input 
                    type="text" 
                    value={this.state.inputValue}
                    placeholder="Enter new task" 
                    onChange={this.updateInputValue}
                    onSubmit={(e) => this.addNewTask(e, newTaskTitle)}/>
                <button className="btn">+</button>
            </div>
        )
    }
}
