import React, { Component } from 'react';

import NewCard from './NewCard';
import NewTaskList from './NewTaskList';

export default class NewCardForm extends Component {
    constructor() {
        super();

        this.state = {
            inputValue: "",
            descriptionValue: "",
            tasks: [],
            clicked: false,
            priority: 'low',
        }

        this.updateInputValue = this.updateInputValue.bind(this);
    }

    updateInputValue = (e) => {
        let updatedInput = e.target.value;
        this.setState({inputValue: updatedInput});
    };

    updateDescription = (e) => {
        let updatedDescr = e.target.value;
        this.setState({descriptionValue: updatedDescr});
    };

    clicked = (e) => {
        this.setState({clicked: true})
    }

    handleSubmit = (e) => { e.preventDefault() }

    handleRatioChange = (e) => {
        this.setState({priority: e.target.value})
    }

    render() {

        if(this.state.clicked) {
            return <NewCard 
                cardTitle={this.state.inputValue} 
                description={this.state.descriptionValue} />
        }

        const {onCancel} = this.props;

            return (
                <div className="NewCard" onSubmit={this.handleSubmit}>
                    <label htmlFor="card-title" className="NewCard__label">Title</label>
                    <input 
                        type="text" 
                        value={this.state.inputValue}
                        placeholder="..." 
                        className="NewCard__title"
                        onChange={e => this.updateInputValue(e)}
                        cardtitle={this.state.inputValue} />

                    <label htmlFor="card-description" className="NewCard__label">Description</label>
                    <textarea 
                        type="text"
                        id="card-descriprtion"
                        value={this.state.descriptionValue}
                        placeholder="..." 
                        className="NewCard__description"
                        onChange={e => this.updateDescription(e)}
                        description={this.state.descriptionValue} />

                    <label htmlFor="task-list" className="NewCard__label">Here you can add list of tasks to your card :</label>
                    <form className="NewCard__taskform">
                        <button>+</button>
                        <input type="sumbit" placeholder="Enter task"/>
                        {/* <NewTaskList newTasks={this.state.tasks}/> */}
                    </form>

                    <form>
                        <legend>Select priority level</legend>
                            <div>
                                <input type="radio" id="low"
                                        name="drone" value="low" 
                                        checked={this.state.priority === 'low'}
                                        onChange={this.handleRatioChange} />
                                <label htmlFor="low">low</label>
                            </div>

                            <div>
                                <input type="radio" id="medium"
                                        name="drone" value="medium" 
                                        checked={this.state.priority === 'medium'}
                                        onChange={this.handleRatioChange} />
                                <label htmlFor="medium">medium</label>
                            </div>

                            <div>
                                <input type="radio" id="high"
                                        name="drone" value="high" level={this.state.priority[2]} />
                                <label htmlFor="high">high</label>
                            </div>
                    </form>

                    <div>
                        <button 
                            type="submit"
                            className="NewCard__btn add btn"
                            onClick={this.clicked.bind(this)}>+</button>
                        <a 
                            href=""
                            className="NewCard__btn cancel"
                            onClick={onCancel}>cancel</a>
                    </div>
                </div>
        )
    }
}
