import React, { Component } from 'react';

import List from './List';

export default class KanbanBoard extends Component {
    constructor() {
        super(...arguments);

        this.state = {
            cards: [],
        }
    }

    addNewCardHandler = (cardID) => {
        let cards = this.props.cards;
        cards.splice(cardID, 0, cards);
        console.log(cards);        
    }

    render() {
        let cardID = this.props.cards.filter(card => card.id === '1')
        return(
            <div className="KanbanBoard">
                <List 
                    title="To Do:"
                    cards={this.props.cards.filter(card => card.status === "to-do")}
                    addNewCard={this.addNewCardHandler(cardID).bind(this)} />
                <List 
                    title="In Progress:"
                    cards={this.props.cards.filter(card => card.status === "in-progress")} />
                <List 
                    title="Completed:"
                    cards={this.props.cards.filter(card => card.status === "completed")} />
            </div>
        )
    }
}
