import React, { Component } from 'react';

import Card from './Card';

export default class List extends Component {
    constructor() {
        super();

        this.state = {
            inputValue: "",
            submitted: false,
            deletedCard: false,
        };

        this.updateInputValue = this.updateInputValue.bind(this);
        this.addNewTaskHandler = this.addNewTaskHandler.bind(this);
    }

    updateInputValue(e) {
            let updatedValue = e.target.value;
            this.setState({inputValue: updatedValue, submitted: true});
            // e.target.value = '';
            console.log(this.state.inputValue);
        // let updatedValue = e.target.value;
        // this.setState({inputValue: updatedValue, submitted: true});
        // console.log(updatedValue);
    }

    addNewTaskHandler(e)  {
        let inputValue = this.state.inputValue.value;
        let submitted = this.state.submitted;

        if( inputValue !== "" && submitted === true ) {
            return (
                <div className="Card">
                    {inputValue}
                </div>
            )
            // return new card with title
        } else return null;
        // <Card {taskTitle} />
    }

    deleteCardHandler = (cardID) => {
        let cards = this.props.cards;
        let cardToDelete = this.props.cards.indexOf(cardID);
        cards.splice(cardToDelete, 1);
        this.setState({deletedCard: true});
        console.log(cards); 
    }

    render() {
        let cards = this.props.cards.map((card) => {
            if(!this.state.deletedCard) {
                return ( 
                    <Card  
                        {...card} 
                        key={card.id} 
                        deleteCard={() => this.deleteCardHandler(card.id).bind(this)} />
                )
            } else return null;
            // return ( 
            //     <Card  
            //         {...card} 
            //         key={card.id} 
            //         deleteCard={() => this.deleteCardHandler(card.id).bind(this)} />
                // <Card id={card.id} 
                //     key={card.id}
                //     cardTitle={card.cardTitle}
                //     description={card.description}
                //     status={card.status}
                //     level={card.level}
                //     tasks={card.taskTitle} /> 
        });

        // const update = ((e) => this.updateInputValue(e));

        // let newCardTitle = '';
        // if( this.state.submitted ) {
        //     newCardTitle.append(this.state.inputValue);
        //     console.log(newCardTitle);
        // }

        // function for task title container -> paste task into card with addNewTaskHandler()

        return(
            <div className="List">
                <h1 className="List__title">{this.props.title}</h1>
                {cards}
                {/* <h1>{newCardTitle}</h1> */}
                <form className="List__form">
                    <input 
                        type="text" 
                        value={this.state.inputValue}
                        placeholder="Enter new task"
                        onChange={this.updateInputValue} />
                    <button type="submit" className="btn">+</button>
                </form>
            </div>
        )
    }
}
