import Card from './Card';
import NewCardForm from '../nestedComponents/NewCardForm';
import NewCard from '../nestedComponents/NewCard';

export default class List extends Component {
    constructor() {
        super();

        this.state = {
            inputValue: "",
            submitted: false,
            cards: [],
            addNewClicked: false,
        };

        this.updateInputValue = this.updateInputValue.bind(this);
        this.deleteCardHandler = this.deleteCardHandler.bind(this);
        // this.addNewCardHandler = this.addNewCardHandler.bind(this);

    }

    updateInputValue = (e) => {
            let updatedValue = e.target.value;
            this.setState({inputValue: updatedValue, submitted: true});
            console.log(this.state.inputValue);
    }

    ////////////
    // addNewCardHandler = (e) => {
    //     this.setState((prevState) => ({
    //         cards: [...prevState.cards]
    //     }));
    //     console.log(prevState);
    // }
    ////////////////

    deleteCardHandler = (cardID) => {
        let cards = this.props.cards;
        let cardToDelete = cards.indexOf(cardID);
        cards.splice(cardToDelete, 1);
        this.setState({cards: cards, addNewClicked: false});
    }

    // addNewCard = (cardID) => {
    //     let cards = [...this.props.cards];
    //     let oldCard = cards.indexOf(cardID) + 1;
    //     this.setState({cards: oldCard});
    // }

    clickedBtn = () => {
        this.setState({addNewClicked: true});
    }

    add = () => {
        console.log(<NewCard />)
    }

    addCARDPLEASE = (cardStatus, cardID) => {
        let cards = this.props.cards;
        let oldCard = cards.indexOf(cardID);
        let newCard = oldCard.push(cards);
        if (cardStatus === "to-do") {
            console.log(newCard)
        } else {
            // cardToAdd = cardToAdd[cardID] + 1;
            this.setState({cards: newCard});
            console.log(oldCard);
        }
    }

    render() {
        let cards = this.props.cards.map((card) => {
            return ( 
                <Card  {...card} 
                    key={card.id} 
                    deleteCard={() => this.deleteCardHandler(card.id)}
                    addCard={() => this.addCARDPLEASE(card.cardStatus, card.id).bind(this)} />
            ) 
        });

        if (this.addNewClicked) {

        }

        let newCardForm = this.state.addNewClicked ? <NewCardForm newCard={this.add.bind(this)}/> : null;

        return(
            <div className="List">
                <h1 className="List__title">{this.props.title}</h1>
                {cards}
                <button 
                    type="button" 
                    className="List__newCard--btn btn"
                    onClick={this.clickedBtn.bind(this)}>Add new card</button>
                {newCardForm}
                <button onClick={this.addCARDPLEASE}>addddd</button>
            </div>
        )
    }
}
