import React, { Component } from 'react';

import TaskList from './TaskList';

export default class Card extends Component {
    constructor() {
        super();

        this.state = {
            taskListEmpty: false,
        }
    }
    render() {
        const getLevelClass = (level) => {
            if(level === 'low') {
                return 'level--low'
            } else if(level === 'medium') {
                return 'level--medium'
            } else return 'level--high'
        }
        const levelClass = getLevelClass(this.props.level);

        const checkTaskList = () => {
            if (this.props.tasks === [{}]) {
                this.setState({taskListEmpty: true})
                console.log(taskListEmpty);
            }
        } 

        return(
            <div className={`Card ${levelClass}`} >
                <div>
                    <h3 className={"Card__title"}>
                        {this.props.cardTitle}
                        <button 
                            className="Card__title--delete btn"
                            onClick={this.props.deleteCard}> delete
                        </button>
                    </h3>
                </div>
                <div className="Card__description">{this.props.description}</div>
                <div>{!this.state.taskListEmpty ? <TaskList tasks={this.props.tasks}/> : null}</div>
                <span className="level">{this.props.level}</span>
            </div>
        )
    }
}
