import React, { Component } from 'react';

import KanbanBoard from './KanbanBoard';

export default class KanbanBoardContainer extends Component {
    constructor() {
        super(...arguments);

        this.state = {
            cards: [],
        }

        this.addNewCardHandler = this.addNewCardHandler.bind(this);
    }

    addNewCardHandler = (cards) => {
        let cardIndex = cards.findIndex((card)=>card.id === cards.id);
        let card = cardIndex[cards];
        cards.splice(cardIndex, 0, card);
        console.log(cards);
    }

    render() {
        return(
            <KanbanBoard cards={this.props.cards} addCard={this.addNewCardHandler(this.props.cards)}/>
        )
    }
}
