import React, { Component } from 'react';

import KanbanBoard from './components/KanbanBoard';
import Particles from 'react-particles-js';
import 'babel-polyfill';


class App extends Component {
  render() {
    let cards = [ 
      {
        id: 1,
        cardTitle: 'Read',
        description: 'Read something',
        status: 'to-do',
        level: 'low',
        tasks: [
          {
            id: 1,
            taskTitle: 'The Norwegian wood',
            done: true
          },
          {
            id: 2,
            taskTitle: 'JS Project',
            done: false
          }
        ]
      },

      {
        id: 2,
        cardTitle: 'Not die',
        description: 'Not die till the end of the project',
        status: 'in-progress',
        level: 'medium',
        tasks: [{}]
      },

      {
        id: 3,
        cardTitle: 'Project',
        description: 'Finish this fckng project',
        status: 'completed',
        level: 'high',
        tasks: [
          {
            id: 1,
            taskTitle: 'React project',
            done: true
          }
        ]
      }
      ]

    const params = {
      particles: {
        // line_linked: {
        //   shadow: {
        //     enable: true,
        //     color: "#5DADE2",
        //     // blur: 7
        //   }
        // },
        line_linked: {
          color: "#000000",
        },
        number: {
          value: 200,
          // density: {
          //   enable: true,
          //   value_area: 800
          // }
        }
      }
    }

    // addNewCardHandler = (cardID) => {
    //   let cardIndex = lists.cards.findIndex((card) => card.id === cardID);
    //       cards.append(cardIndex);
    //       this.setState({cards: cards});
    //       console.log(cards);
    // }

    return (
      <div className="KanbanBoard">
        <KanbanBoard cards={cards} />
        <Particles className="App" params={params} />
      </div>
    );
  }
}

export default App;
