import React, { Component } from 'react';

import Card from './Card';

export default class List extends Component {
    constructor() {
        super();

        this.state = {
            inputValue: '',
            submitted: false,
        };

        this.updateInputValue = this.updateInputValue.bind(this);
        this.addNewTaskHandler = this.addNewTaskHandler.bind(this);
    }

    updateInputValue(e)  {
        let updatedValue = e.target.value;
        this.setState({inputValue: updatedValue, submitted: true});
        console.log(updatedValue);
    }

    addNewTaskHandler(e)  {
        let inputValue = this.state.inputValue.value;
        let submitted = this.state.submitted;

        if( inputValue !== '' && submitted === true ) {
            return (
                <div className="Card">
                    {inputValue}
                </div>
            )
            // return new card with title
        } else return null;
        // <Card {taskTitle} />
    }

    addNewCardHandler() {

    }

    render() {
        let cards = this.props.cards.map((card) => {
            return ( <Card  {...card} key={card.id}/>
                // <Card id={card.id} 
                //     key={card.id}
                //     cardTitle={card.cardTitle}
                //     description={card.description}
                //     status={card.status}
                //     level={card.level}
                //     tasks={card.taskTitle} /> 
            )
        });

        let newCardTitle = null;
        if( this.state.submitted ) {
            newCardTitle = this.state.inputValue.textContent;
            console.log(newCardTitle);
        }

        // function for task title container -> paste task into card with addNewTaskHandler()

        return(
            <div className="List">
                <h1 className="List__title">{this.props.title}</h1>
                {cards}
                <h1>{newCardTitle}</h1>
                <form onSubmit={this.addNewTaskHandler}>
                    <input 
                        type="text" 
                        value={this.state.inputValue}
                        placeholder="Enter new task" 
                        onChange={this.updateInputValue} />
                    <button type="submit" className="btn">+</button>
                </form>
            </div>
        )
    }
}
