import React, { Component } from 'react';

import KanbanBoard from './KanbanBoard';

export default class KanbanBoardContainer extends Component {
    constructor() {
        super();

        this.state = {
            cards: [],
        }

        this.addNewCardHandler = this.addNewCardHandler.bind(this);
    }

    addNewCardHandler = (cardID) => {
        let cardIndex = this.props.cards.findIndex((card)=>card.id === cards.id);
        let card = cardIndex[cardID];
        cards.splice(cardIndex, 0, card);
        this.setState({cards: cards})
    }

    render() {

        return(
            <KanbanBoard cards={this.props.cards} addCard={this.addNewCardHandler(this.props.cardID)}/>
        )
    }
}
